mapper.select:
/* GoodsMapper.getAdminGoodsList: 관리자 상품 목록 조회 */ SELECT GB.GOODS_ID , GB.BRAND_NO , BB.BRAND_NM AS BRAND_NM , GB.ERP_STYLE_CD , GB.GOODS_NM , GB.GOODS_STAT_CD , CS.CD_NM AS GOODS_STAT_NM , GB.GOODS_DIV_CD , CD.CD_NM AS GOODS_DIV_NM , GB.SHOW_YN , GB.REG_DT , GB.UDT_DT FROM GOODS_BASE GB LEFT JOIN BRAND_BASE BB ON BB.BRAND_NO = GB.BRAND_NO LEFT JOIN COMMON_CODE CD ON CD.GRP_CD = 'GOODS_DIV' AND CD.CD = GB.GOODS_DIV_CD LEFT JOIN COMMON_CODE CS ON CS.GRP_CD = 'GOODS_STAT' AND CS.CD = GB.GOODS_STAT_CD WHERE 1 = 1
AND GB.GOODS_STAT_CD = #{goodsStatCd}
AND GB.GOODS_DIV_CD = #{goodsDivCd}
AND GB.SHOW_YN = #{showYn}
AND GB.BRAND_NO = #{brandNo}
AND GB.GOODS_ID LIKE CONCAT(#{searchValue}, '%')
AND GB.ERP_STYLE_CD LIKE CONCAT(#{searchValue}, '%')
AND MATCH(GB.GOODS_NM) AGAINST(#{searchKeyword} IN BOOLEAN MODE)
ORDER BY GB.REG_DT DESC LIMIT #{pageSize} OFFSET #{offset}

/* GoodsMapper.getAdminGoodsCount: 관리자 상품 목록 조회 수 */
SELECT COUNT(1)
  FROM GOODS_BASE GB
 WHERE 1 = 1

   AND GB.GOODS_STAT_CD = #{goodsStatCd}


   AND GB.GOODS_DIV_CD = #{goodsDivCd}


   AND GB.SHOW_YN = #{showYn}


   AND GB.BRAND_NO = #{brandNo}




   AND GB.GOODS_ID LIKE CONCAT(#{searchValue}, '%')


   AND GB.ERP_STYLE_CD LIKE CONCAT(#{searchValue}, '%')


   AND MATCH(GB.GOODS_NM) AGAINST(#{searchKeyword} IN BOOLEAN MODE)

mapper.insert:
/* GoodsMapper.insertAdminGoods: 관리자 상품 등록 */
INSERT INTO GOODS_BASE (
       GOODS_ID
     , BRAND_NO
     , GOODS_DIV_CD
     , GOODS_STAT_CD
     , GOODS_NM
     , GOODS_GROUP_ID
     , GOODS_MERCH_ID
     , SUPPLY_AMT
     , SALE_AMT
     , SHOW_YN
     , ERP_SUPPLY_AMT
     , ERP_COST_AMT
     , ERP_STYLE_CD
     , ERP_COLOR_CD
     , ERP_MERCH_CD
     , REG_NO
     , UDT_NO
) VALUES (
       #{goodsId}
     , #{brandNo}
     , #{goodsDivCd}
     , #{goodsStatCd}
     , #{goodsNm}
     , #{goodsGroupId}
     , #{goodsMerchId}
     , #{supplyAmt}
     , #{saleAmt}
     , #{showYn}
     , #{erpSupplyAmt}
     , #{erpCostAmt}
     , #{erpStyleCd}
     , #{erpColorCd}
     , #{erpMerchCd}
     , #{regNo}
     , #{udtNo}
)

mapper.update:
/* GoodsMapper.updateAdminGoods: 관리자 상품 수정 */
UPDATE GOODS_BASE
   SET GOODS_DIV_CD = #{goodsDivCd}
     , GOODS_STAT_CD = #{goodsStatCd}
     , GOODS_NM = #{goodsNm}
     , GOODS_GROUP_ID = #{goodsGroupId}
     , GOODS_MERCH_ID = #{goodsMerchId}
     , SUPPLY_AMT = #{supplyAmt}
     , SALE_AMT = #{saleAmt}
     , SHOW_YN = #{showYn}
     , ERP_SUPPLY_AMT = #{erpSupplyAmt}
     , ERP_COST_AMT = #{erpCostAmt}
     , ERP_STYLE_CD = #{erpStyleCd}
     , ERP_COLOR_CD = #{erpColorCd}
     , ERP_MERCH_CD = #{erpMerchCd}
     , UDT_NO = #{udtNo}
     , UDT_DT = NOW()
 WHERE GOODS_ID = #{goodsId}

mapper.select:
/* GoodsMapper.getCategoryList: 카테고리 목록 조회 */ SELECT CATEGORY_ID , PARENT_CATEGORY_ID , CATEGORY_LEVEL , CATEGORY_NM , DISP_ORD , SHOW_YN FROM CATEGORY WHERE SHOW_YN = 'Y'
AND CATEGORY_LEVEL = #{categoryLevel}
AND PARENT_CATEGORY_ID = #{parentCategoryId}
ORDER BY DISP_ORD ASC, CATEGORY_ID ASC

/* GoodsMapper.getAdminCategoryMaxDispOrd: 관리자 카테고리 정렬 순서 최대값 조회 */
SELECT COALESCE(MAX(DISP_ORD), 0)
  FROM CATEGORY
 WHERE PARENT_CATEGORY_ID = #{parentCategoryId}

/* GoodsMapper.getAdminCategoryMaxId: 관리자 카테고리 최대 코드 조회 */
SELECT MAX(CATEGORY_ID)
  FROM CATEGORY
 WHERE PARENT_CATEGORY_ID = #{parentCategoryId}

mapper.insert:
/* GoodsMapper.insertAdminCategory: 관리자 카테고리 등록 */
INSERT INTO CATEGORY (
       CATEGORY_ID
     , PARENT_CATEGORY_ID
     , CATEGORY_LEVEL
     , CATEGORY_NM
     , DISP_ORD
     , SHOW_YN
     , REG_NO
     , UDT_NO
) VALUES (
       #{categoryId}
     , #{parentCategoryId}
     , #{categoryLevel}
     , #{categoryNm}
     , #{dispOrd}
     , #{showYn}
     , #{regNo}
     , #{udtNo}
)

mapper.update:
/* GoodsMapper.updateAdminCategory: 관리자 카테고리 수정 */
UPDATE CATEGORY
   SET CATEGORY_NM = #{categoryNm}
     , DISP_ORD = #{dispOrd}
     , SHOW_YN = #{showYn}
     , UDT_NO = #{udtNo}
     , UDT_DT = NOW()
 WHERE CATEGORY_ID = #{categoryId}

mapper.delete:
/* GoodsMapper.deleteAdminCategory: 관리자 카테고리 삭제 처리 */
DELETE
  FROM CATEGORY
 WHERE CATEGORY_ID = #{categoryId}

mapper.select:
/* GoodsMapper.countAdminCategoryChildren: 관리자 카테고리 하위 건수 조회 */
SELECT COUNT(1)
  FROM CATEGORY
 WHERE PARENT_CATEGORY_ID = #{categoryId}

/* GoodsMapper.countAdminCategoryById: 관리자 카테고리 중복 건수 조회 */
SELECT COUNT(1)
  FROM CATEGORY
 WHERE CATEGORY_ID = #{categoryId}

/* GoodsMapper.countAdminCategoryGoods: 관리자 카테고리 상품 사용 건수 조회 */
SELECT COUNT(1)
  FROM GOODS_CATEGORY
 WHERE CATEGORY_ID = #{categoryId}

/* GoodsMapper.countCategoryChildren: 카테고리 하위 건수 조회 */
SELECT COUNT(1)
  FROM CATEGORY
 WHERE PARENT_CATEGORY_ID = #{categoryId}
   AND SHOW_YN = 'Y'

mapper.delete:
/* GoodsMapper.deleteAdminGoodsCategory: 관리자 상품 카테고리 단건 삭제 */
DELETE
  FROM GOODS_CATEGORY
 WHERE GOODS_ID = #{goodsId}
   AND CATEGORY_ID = #{categoryId}

/* GoodsMapper.deleteAdminGoodsCategoryByGoodsId: 관리자 상품 카테고리 삭제 */
DELETE
  FROM GOODS_CATEGORY
 WHERE GOODS_ID = #{goodsId}

mapper.insert:
/* GoodsMapper.insertAdminGoodsCategoryList: 관리자 상품 카테고리 등록 */
INSERT INTO GOODS_CATEGORY (
       GOODS_ID
     , CATEGORY_ID
     , DISP_ORD
     , REG_NO
     , UDT_NO
) VALUES

	(
       #{item.goodsId}
     , #{item.categoryId}
     , #{item.dispOrd}
     , #{item.regNo}
     , #{item.udtNo}
	)

/* GoodsMapper.insertAdminGoodsCategory: 관리자 상품 카테고리 단건 등록 */
INSERT INTO GOODS_CATEGORY (
       GOODS_ID
     , CATEGORY_ID
     , DISP_ORD
     , REG_NO
     , UDT_NO
) VALUES (
       #{goodsId}
     , #{categoryId}
     , #{dispOrd}
     , #{regNo}
     , #{udtNo}
)

mapper.update:
/* GoodsMapper.updateAdminGoodsCategory: 관리자 상품 카테고리 단건 수정 */
UPDATE GOODS_CATEGORY
   SET DISP_ORD = #{dispOrd}
     , UDT_NO = #{udtNo}
     , UDT_DT = NOW()
 WHERE GOODS_ID = #{goodsId}
   AND CATEGORY_ID = #{categoryId}

mapper.select:
/* GoodsMapper.countAdminGoodsCategory: 관리자 상품 카테고리 건수 조회 */
SELECT COUNT(1)
  FROM GOODS_CATEGORY
 WHERE GOODS_ID = #{goodsId}
   AND CATEGORY_ID = #{categoryId}

mapper.insert:
/* GoodsMapper.insertAdminGoodsSize: 관리자 상품 사이즈 등록 */
INSERT INTO GOODS_SIZE (
       GOODS_ID
     , SIZE_ID
     , STOCK_QTY
     , ADD_AMT
     , ERP_SYNC_YN
     , ERP_SIZE_CD
     , DISP_ORD
     , DEL_YN
     , REG_NO
     , UDT_NO
) VALUES (
       #{goodsId}
     , #{sizeId}
     , #{stockQty}
     , #{addAmt}
     , #{erpSyncYn}
     , #{erpSizeCd}
     , #{dispOrd}
     , #{delYn}
     , #{regNo}
     , #{udtNo}
)

mapper.update:
/* GoodsMapper.updateAdminGoodsSize: 관리자 상품 사이즈 수정 */
UPDATE GOODS_SIZE
   SET SIZE_ID = #{sizeId}
     , STOCK_QTY = #{stockQty}
     , ADD_AMT = #{addAmt}
     , ERP_SYNC_YN = #{erpSyncYn}
     , DISP_ORD = #{dispOrd}
     , UDT_NO = #{udtNo}
     , UDT_DT = NOW()
 WHERE GOODS_ID = #{goodsId}
   AND SIZE_ID = #{originSizeId}

/* GoodsMapper.deleteAdminGoodsSize: 관리자 상품 사이즈 삭제 */
UPDATE GOODS_SIZE
   SET DEL_YN = 'Y'
     , UDT_NO = #{udtNo}
     , UDT_DT = NOW()
 WHERE GOODS_ID = #{goodsId}
   AND SIZE_ID = #{sizeId}

/* GoodsMapper.updateAdminGoodsSizeOrder: 관리자 상품 사이즈 순서 저장 */
UPDATE GOODS_SIZE
   SET DISP_ORD = CASE SIZE_ID

	WHEN #{order.sizeId} THEN #{order.dispOrd}

	END
     , UDT_NO = #{udtNo}
     , UDT_DT = NOW()
 WHERE GOODS_ID = #{goodsId}
   AND SIZE_ID IN

	#{order.sizeId}

mapper.select:
/* GoodsMapper.getAdminGoodsImageMaxDispOrd: 관리자 상품 이미지 정렬순서 최대값 조회 */
SELECT COALESCE(MAX(DISP_ORD), 0)
  FROM GOODS_IMG
 WHERE GOODS_ID = #{goodsId}

mapper.insert:
/* GoodsMapper.insertAdminGoodsImage: 관리자 상품 이미지 등록 */
