     , DISP_ORD
     , IMG_PATH
     , REG_NO
     , UDT_NO
) VALUES (
       #{goodsId}
     , #{dispOrd}
     , #{imgPath}
     , #{regNo}
     , #{udtNo}
)

mapper.delete:
/* GoodsMapper.deleteAdminGoodsImage: 관리자 상품 이미지 삭제 */
DELETE
  FROM GOODS_IMG
 WHERE IMG_NO = #{imgNo}

mapper.update:
/* GoodsMapper.updateAdminGoodsImageOrder: 관리자 상품 이미지 순서 저장 */
UPDATE GOODS_IMG
   SET DISP_ORD = CASE IMG_NO

	WHEN #{order.imgNo} THEN #{order.dispOrd}

	END
     , UDT_NO = #{udtNo}
     , UDT_DT = NOW()
 WHERE GOODS_ID = #{goodsId}
   AND IMG_NO IN

	#{order.imgNo}

mapper.select:
/* GoodsMapper.getAdminGoodsDescList: 관리자 상품 상세 설명 목록 조회 */
SELECT GOODS_ID
     , DEVICE_GB_CD
     , GOODS_DESC
  FROM GOODS_DESC
 WHERE GOODS_ID = #{goodsId}

/* GoodsMapper.countAdminGoodsDesc: 관리자 상품 상세 설명 건수 조회 */
SELECT COUNT(1)
  FROM GOODS_DESC
 WHERE GOODS_ID = #{goodsId}
   AND DEVICE_GB_CD = #{deviceGbCd}

mapper.insert:
