       PRESS_NO
     , CATEGORY_CD
     , ARTICLE_GUID
     , ARTICLE_GUID_HASH
     , ARTICLE_URL
     , ARTICLE_URL_HASH
     , ARTICLE_TITLE
     , ARTICLE_SUMMARY
     , THUMBNAIL_URL
     , AUTHOR_NM
     , PUBLISHED_DT
     , COLLECTED_DT
     , RANK_SCORE
     , USE_YN
     , REG_NO
     , UDT_NO
) VALUES (
       #{pressNo}
     , #{categoryCd}
     , #{articleGuid}
     , NULLIF(#{articleGuidHash}, '')
     , #{articleUrl}
     , #{articleUrlHash}
     , #{articleTitle}
     , #{articleSummary}
     , #{thumbnailUrl}
     , #{authorNm}
     , #{publishedDt}
     , NOW()
     , #{rankScore}
     , #{useYn}
     , NULL
     , NULL
)
