		NULL

	, 

			STR_TO_DATE(CONCAT(#{detail.workEndDt}, '-01'), '%Y-%m-%d')

		NULL

	, #{detail.workDesc}
	, 'N'
	, #{usrNo}
	, #{usrNo}
	)
